CREATE VIEW [crt].[REMOTEPRODUCTASSORTMENTRULESVIEW]
AS
(
    -- Retrieve temporarily assorted products that have been downloaded by way of virtual catalog.
    SELECT
        tap.CHANNELID As CHANNELID,
        tap.PRODUCTID AS 'PRODUCTID',
        0 AS 'VARIANTID',
        1 AS 'ISREMOTE',
        tap.VALIDFROM,
