        tap.CHANNELID AS CHANNELID,
        tap.PRODUCTID AS 'PRODUCTID',
        pv.RECID AS 'VARIANTID',
        1 AS 'ISREMOTE',
        tap.VALIDFROM,
        tap.VALIDTO
