CREATE FUNCTION [crt].[GETPUBCATALOG]
(
    @bi_ChannelId       BIGINT,
    @dt_ChannelDate     DATE,
    @b_IsForwardLooking BIT
)
RETURNS TABLE
RETURN
    SELECT
