(
    SELECT
        era.RECID AS ATTRIBUTE,
        erat.RECID AS ATTRIBUTETYPE,
        erat.DATATYPE
        FROM ax.ECORESATTRIBUTE era
            JOIN ax.ECORESATTRIBUTETYPE erat 
                ON erat.RECID = era.ATTRIBUTETYPE
        WHERE erat.DATATYPE != 20   -- Non reference attribute type

