    FROM [crt].GETPRODUCTCATEGORY(@bi_ChannelId, @bi_CatalogId, @dt_ChannelDate, 1 /* @b_ProductIdsPassed */, @tvp_ProductIds, @tvp_DummyId) pc
	INNER JOIN [crt].CATEGORYPRODUCTATTRIBUTEVIEW cpav ON cpav.CATEGORY = pc.CATEGORY

