)
AS
BEGIN
    DECLARE @tvp_SubCategoryIds [crt].[RECORDIDTABLETYPE]

    ;WITH CategoryHierarchyTree (RECID)
    AS
    (
        SELECT [cchv].RECID
        FROM [crt].CHANNELCATEGORYHIERARCHYVIEW cchv
        WHERE [cchv].RECID = @bi_CategoryId AND [cchv].CHANNELID = @bi_ChannelId

        UNION ALL

