                BEGIN
                    WAITFOR DELAY @vc_WaitTime;
                END
                SET @bi_InnerWaitTrigger = @bi_InnerTableRowcount/@i_WaitRows;
            END
        END

        SET @bi_InnerTableRowcount = 0;
        SET @bi_InnerWaitTrigger   = 0;
        SET @f_ExitInnerLoop = 0;
