CREATE PROCEDURE [crt].[GETACTIVESHIFT]
    @bi_ChannelId       BIGINT,
    @nvc_CurrentTerminalId     NVARCHAR(10),
    @nvc_UserId         NVARCHAR(50)
AS
BEGIN
    SET NOCOUNT ON

    DECLARE @i_ReturnCode               INT;
    DECLARE @i_Error                    INT;

