    SET @i_ReturnCode = 0;

    -- Select active shift identifier.
    SELECT [SHIFTID],
           [TERMINALID]
    FROM [crt].SHIFTSVIEW SHIFT
    WHERE SHIFT.CHANNEL = @bi_ChannelId AND
