CREATE PROCEDURE [crt].[INSERTADDRESSTRANS]
    @bi_ChannelId           BIGINT,
    @TVP_AddressTrans       [crt].[RETAILTRANSACTIONADDRESSTRANSTABLETYPE]    READONLY
AS
BEGIN

    SET NOCOUNT ON;

    DECLARE @i_ReturnCode               INT;
    DECLARE @i_TransactionIsOurs        INT;
    DECLARE @i_Error                    INT;

    -- initializes the return code and assume the transaction is not ours by default
    SET @i_ReturnCode = 0;
    SET @i_TransactionIsOurs = 0;

    IF @@TRANCOUNT = 0
    BEGIN
        BEGIN TRANSACTION;

        SELECT @i_Error = @@ERROR;
        IF @i_Error <> 0
        BEGIN
            SET @i_ReturnCode = @i_Error;
            GOTO exit_label;
        END;

        SET @i_TransactionIsOurs = 1;
    END;

    -- Perform Insert
    INSERT INTO [ax].RETAILTRANSACTIONADDRESSTRANS
    (
        [DATAAREAID],
        [STORE],
        [TERMINAL],
        [TRANSACTIONID],
        [SALELINENUM],
        [DELIVERYNAME],
        [SALESNAME],
        [ZIPCODE],
        [COUNTRYREGIONID],
        [STATE],
        [CITY],
        [COUNTY],
        [STREET],
        [EMAIL],
		[EMAILCONTENT],
        [PHONE],
        [STREETNUMBER],
        [DISTRICTNAME],
        [CHANNEL]
    )
    SELECT
        [DATAAREAID],
        [STORE],
        [TERMINAL],
        [TRANSACTIONID],
        [SALELINENUM],
        [DELIVERYNAME],
        [SALESNAME],
        [ZIPCODE],
        [COUNTRYREGIONID],
        [STATE],
        [CITY],
        [COUNTY],
        [STREET],
        [EMAIL],
		[EMAILCONTENT],
        [PHONE],
        [STREETNUMBER],
        [DISTRICTNAME],
        @bi_ChannelId
    FROM @TVP_AddressTrans AS Source;

    SELECT @i_Error = @@ERROR;
    IF @i_Error <> 0
    BEGIN
        SET @i_ReturnCode = @i_Error;
        GOTO exit_label;
    END;

    IF @i_TransactionIsOurs = 1
    BEGIN
        COMMIT TRANSACTION;

        SET @i_Error = @@ERROR;
        IF @i_Error <> 0
        BEGIN
            SET @i_ReturnCode = @i_Error;
            GOTO exit_label;
        END;

        SET @i_TransactionIsOurs = 0;
    END;

exit_label:

    IF @i_TransactionIsOurs = 1
    BEGIN
        ROLLBACK TRANSACTION;
    END;

    RETURN @i_ReturnCode;
END;
