CREATE PROCEDURE [crt].[INSERTDISCOUNTTRANS]
    @bi_channelId           BIGINT,
    @TVP_DiscountTrans      [crt].[RETAILTRANSACTIONDISCOUNTTRANSTABLETYPE]    READONLY
AS
BEGIN

    SET NOCOUNT ON;

    DECLARE @i_ReturnCode               INT;
    DECLARE @i_TransactionIsOurs        INT;
    DECLARE @i_Error                    INT;

    -- initializes the return code and assume the transaction is not ours by default
    SET @i_ReturnCode = 0;
    SET @i_TransactionIsOurs = 0;

    IF @@TRANCOUNT = 0
    BEGIN
        BEGIN TRANSACTION;

        SELECT @i_Error = @@ERROR;
        IF @i_Error <> 0
        BEGIN
            SET @i_ReturnCode = @i_Error;
            GOTO exit_label;
        END;

        SET @i_TransactionIsOurs = 1;
    END;

    -- Perform Insert
    INSERT INTO [ax].RETAILTRANSACTIONDISCOUNTTRANS
    (
        [AMOUNT],        
        [DATAAREAID],
        [DEALPRICE],
        [DISCOUNTAMOUNT],
        [DISCOUNTCODE],
        [LINENUM],         
        [PERCENTAGE],
        [PERIODICDISCOUNTOFFERID],
        [SALELINENUM],
        [STOREID],
        [TERMINALID],
        [TRANSACTIONID],
        [CHANNEL],
        [DISCOUNTORIGINTYPE],
        [MANUALDISCOUNTTYPE],
        [CUSTOMERDISCOUNTTYPE] 
    )
    SELECT
        [AMOUNT],
        [DATAAREAID],
        [DEALPRICE],
        [DISCOUNTAMOUNT],
        [DISCOUNTCODE],
        [LINENUM],
        [PERCENTAGE],
        [PERIODICDISCOUNTOFFERID],
        [SALELINENUM],
        [STOREID],
        [TERMINALID],
        [TRANSACTIONID],
        @bi_channelId,
        [DISCOUNTORIGINTYPE],
        [MANUALDISCOUNTTYPE],
        [CUSTOMERDISCOUNTTYPE]   
    FROM @TVP_DiscountTrans AS Source;

    SELECT @i_Error = @@ERROR;
    IF @i_Error <> 0
    BEGIN
        SET @i_ReturnCode = @i_Error;
        GOTO exit_label;
    END;

    IF @i_TransactionIsOurs = 1
    BEGIN
        COMMIT TRANSACTION;

        SET @i_Error = @@ERROR;
        IF @i_Error <> 0
        BEGIN
            SET @i_ReturnCode = @i_Error;
            GOTO exit_label;
        END;

        SET @i_TransactionIsOurs = 0;
    END;

exit_label:

    IF @i_TransactionIsOurs = 1
    BEGIN
        ROLLBACK TRANSACTION;
    END;

    RETURN @i_ReturnCode;
END;
