            SET @bi_WaitTrigger = @bi_TableRowcount/@i_WaitRows;
        END;
    END;

exit_label:
    IF @vc_Error IS NOT NULL
    BEGIN
        RAISERROR(@vc_Error, 16, 1);
    END;
    ELSE
    BEGIN
        RAISERROR(@vc_Success, 10, 1);
    END;
END;
