CREATE PROCEDURE [crt].[INSERTCUSTOMERORDERTRANSACTION]
    @bi_channelId           BIGINT,
    @TVP_CUSTOMERORDERTRANS [crt].[CUSTOMERORDERTRANSACTIONTABLETYPE]          READONLY
AS
BEGIN

    SET NOCOUNT ON;

    DECLARE @i_ReturnCode               INT;
    DECLARE @i_TransactionIsOurs        INT;
    DECLARE @i_Error                    INT;

    -- initializes the return code and assume the transaction is not ours by default
    SET @i_ReturnCode = 0;
    SET @i_TransactionIsOurs = 0;

    IF @@TRANCOUNT = 0
    BEGIN
        BEGIN TRANSACTION;

        SELECT @i_Error = @@ERROR;
        IF @i_Error <> 0
        BEGIN
            SET @i_ReturnCode = @i_Error;
            GOTO exit_label;
        END;

        SET @i_TransactionIsOurs = 1;
    END;

    -- Perform Insert
    INSERT INTO [crt].CUSTOMERORDERTRANSACTION
    (
	[CHANNEL],
	[STORE],
	[TERMINAL],
	[TRANSACTIONID],
	[DATAAREAID],
	[CANCELLATIONCHARGE],
	[DEPOSITOVERRIDE],
	[REQUIREDDEPOSIT],
	[CALCULATEDDEPOSIT],
	[PREPAYMENTPAID],
	[PREPAYMENTINVOICED],
	[QUOTEEXPIRATIONDATE],
	[CUSTOMERORDERTYPE],
	[CUSTOMERORDERMODE]
    )
    SELECT
	@bi_channelId,
	[STORE],
	[TERMINAL],
	[TRANSACTIONID],
	[DATAAREAID],
	[CANCELLATIONCHARGE],
	[DEPOSITOVERRIDE],
	[REQUIREDDEPOSIT],
	[CALCULATEDDEPOSIT],
	[PREPAYMENTPAID],
	[PREPAYMENTINVOICED],
	[QUOTEEXPIRATIONDATE],
	[CUSTOMERORDERTYPE],
	[CUSTOMERORDERMODE]
    FROM @TVP_CUSTOMERORDERTRANS AS Source;

    SELECT @i_Error = @@ERROR;
    IF @i_Error <> 0
    BEGIN
        SET @i_ReturnCode = @i_Error;
        GOTO exit_label;
    END;

    IF @i_TransactionIsOurs = 1
    BEGIN
        COMMIT TRANSACTION;

        SET @i_Error = @@ERROR;
        IF @i_Error <> 0
        BEGIN
            SET @i_ReturnCode = @i_Error;
            GOTO exit_label;
        END;

        SET @i_TransactionIsOurs = 0;
    END;

exit_label:

    IF @i_TransactionIsOurs = 1
    BEGIN
        ROLLBACK TRANSACTION;
    END;

    RETURN @i_ReturnCode;
END;
