        ,[ISPREPAYMENT]
        ,[LOYALTYCARDID]
        ,[CARDORACCOUNT]
        ,[CARDTYPEID]
        ,[CREDITVOUCHERID]
        ,[TRANSDATE]
        ,[TRANSTIME]
        ,[QTY]
        ,[CHANNEL]
        ,[BUSINESSDATE]
    )
    SELECT
        [AMOUNTCUR]
        ,[TENDERCUR]
        ,[EXCHRATECUR] * 100.00 -- ePOS behavior
        ,[AMOUNTTENDERED]
        ,[EXCHRATEMST] * 100.00 -- ePOS behavior
        ,[AMOUNTMST]
        ,[DATAAREAID]
        ,[LINENUM]
        ,[PAYMENTCARDTOKEN]
        ,[PAYMENTAUTHORIZATION]
        ,[ISPAYMENTCAPTURED]
        ,[STORE]
