CREATE PROCEDURE [crt].[INSERTINFOCODETRANS]
    @bi_ChannelId           BIGINT,
    @TVP_InfoCodeTrans      [crt].[RETAILTRANSACTIONINFOCODETRANSTABLETYPE]    READONLY,
    @d_BusinessDate         DATE
AS
BEGIN

    SET NOCOUNT ON;

    DECLARE @i_ReturnCode               INT;
    DECLARE @i_TransactionIsOurs        INT;
    DECLARE @i_Error                    INT;

    -- initializes the return code and assume the transaction is not ours by default
    SET @i_ReturnCode = 0;
    SET @i_TransactionIsOurs = 0;

    IF @@TRANCOUNT = 0
    BEGIN
        BEGIN TRANSACTION;

        SELECT @i_Error = @@ERROR;
        IF @i_Error <> 0
        BEGIN
            SET @i_ReturnCode = @i_Error;
            GOTO exit_label;
        END;

        SET @i_TransactionIsOurs = 1;
    END;

    -- Perform Insert
    INSERT INTO [ax].RETAILTRANSACTIONINFOCODETRANS
    (
        [TRANSACTIONID],
        [LINENUM],
        [DATAAREAID],
        [TYPE],
        [INFOCODEID],
        [INFORMATION],
        [INFOAMOUNT],
        [TRANSDATE],
        [TRANSTIME],
        [STORE],
        [TERMINAL],
        [STAFF],
        [ITEMTENDER],
        [AMOUNT],
        [INPUTTYPE],
        [SUBINFOCODEID],
        [STATEMENTCODE],
        [SOURCECODE],
        [SOURCECODE2],
        [SOURCECODE3],
        [PARENTLINENUM],
        [CHANNEL],
        [BUSINESSDATE]
    )
    SELECT
        [TRANSACTIONID],
        [LINENUM],
        [DATAAREAID],
        [TYPE],
        [INFOCODEID],
        [INFORMATION],
        [INFOAMOUNT],
        CAST([TRANSDATE] AS DATE),
        [TRANSTIME],
        [STORE],
        [TERMINAL],
        [STAFF],
        [ITEMTENDER],
        [AMOUNT],
        [INPUTTYPE],
        [SUBINFOCODEID],
        [STATEMENTCODE],
        [SOURCECODE],
        [SOURCECODE2],
        [SOURCECODE3],
        [PARENTLINENUM],
        @bi_ChannelId,
        @d_BusinessDate
    FROM @TVP_InfoCodeTrans AS Source;

    SELECT @i_Error = @@ERROR;
    IF @i_Error <> 0
    BEGIN
        SET @i_ReturnCode = @i_Error;
        GOTO exit_label;
    END;

    IF @i_TransactionIsOurs = 1
    BEGIN
        COMMIT TRANSACTION;

        SET @i_Error = @@ERROR;
        IF @i_Error <> 0
        BEGIN
            SET @i_ReturnCode = @i_Error;
            GOTO exit_label;
        END;

        SET @i_TransactionIsOurs = 0;
    END;

exit_label:

    IF @i_TransactionIsOurs = 1
    BEGIN
        ROLLBACK TRANSACTION;
    END;

    RETURN @i_ReturnCode;
END;
