			,[LINENUM]
			,[QTY]
		    ,[STOREID]
		    ,[TERMINALID]
		    ,[TRANSACTIONID]
		    ,[TRANSACTIONSTATUS]
		    ,[VARIANTID]
		    ,[DATAAREAID]
